import React, { useState, useRef } from 'react';
import { Splide, SplideSlide } from '@splidejs/react-splide';
import { Splide as SplideInstance } from '@splidejs/splide';
import '@splidejs/react-splide/css';
import Image from 'next/image';
import SectionHeader from '@/components/SectionHeader/SectionHeader';
import SliderNav from '@/components/SliderNav/SliderNav';

const GuestsContainer = () => {
  const [activeSlide, setActiveSlide] = useState(0);
  const splideRef = useRef<SplideInstance | null>(null);

  const prevSlide = () => {
    splideRef.current?.go('<');
  };

  const nextSlide = () => {
    splideRef.current?.go('>');
  };

  return (
    <div className="h-screen relative">
      <div id="guests-section" className="h-full">
        <div className="guests-mobile">
          <SectionHeader headerText="Our Guests" />
          <Splide
            onMove={(splideInstance: any, newIndex: number) => {
              setActiveSlide(newIndex);
            }}
            onMounted={(splide: any) => {
              splideRef.current = splide;
            }}
            aria-label="Our Guests"
            options={{
              type: 'loop',
              drag: 'true',
              snap: true,
              gap: '1.5rem',
              lazyLoad: 'nearby',
              padding: '3rem',
              height: '50vh',
              arrows: false,
              pagination: false,
              speed: 1000,
              easing: 'cubic-bezier( 0.785, 0.135, 0.15, 0.86)',
            }}
          >
            <SplideSlide>
              <Image
                src="data:image/jpeg;base64,"
                alt="avatar 1"
                layout="fill"
                objectFit="cover"
              />
            </SplideSlide>
            <SplideSlide>
              <Image
                src="data:image/jpeg;base64,"
                alt="avatar 2"
                layout="fill"
                objectFit="cover"
              />
            </SplideSlide>
            <SplideSlide>
              <Image
                src="data:image/jpeg;base64,"
                alt="avatar 3"
                layout="fill"
                objectFit="cover"
              />
            </SplideSlide>
            <SplideSlide>
              <Image
                src="data:image/jpeg;base64,"
                alt="avatar 4"
                layout="fill"
                objectFit="cover"
              />
            </SplideSlide>
            <SplideSlide>
              <Image
                src="data:image/jpeg;base64,"
                alt="avatar 5"
                layout="fill"
                objectFit="cover"
              />
            </SplideSlide>
          </Splide>
          <SliderNav
            prevSlide={prevSlide}
            nextSlide={nextSlide}
            currentIndex={activeSlide}
            items={5}
          />
        </div>
      </div>
    </div>
  );
};

GuestsContainer.displayName = 'GuestsContainer';
export default GuestsContainer;
